        if (msg.topic && msg.topic.includes('/homie/')) { // seams to be a homie message
            msgOut = {
                "topic":msg.topic,
                "broker":msg.topic.substring(0,msg.topic.indexOf('/homie/')),
                "payload":msg.payload.$name
            }
            return msgOut;
        }
        break;
    case "updateDevices":
        msgOut = {
            "topic":msg.topic,
            "payload":"[any]"
        }
        return msgOut;
}
return msg;
