const never=0;
const onChange=1;
const allways=2;

// -----------------------------------------------------------------
// example of a generic translator for msg.objects into state values
//
// this is WORK IN PROGRESS!
// share improvements if you think they can be usefull to others
//
// -----------------------------------------------------------------

var translator={
    "$topic":{"properties":["deviceType","device"],"requited":true,"send":allways},
    "$name":{"property":"deviceName","default":"","format":"","requited":true,"send":allways},
    "$state":{"property":"","default":"ready","format":"","requited":false,"send":allways},
    "$localip":{"property":"ccu","default":"127.0.0.1","format":"","requited":false,"send":onChange},
    "$mac":{"property":"","default":"FF:FF:FF:FF:FF:FF","format":"","requited":false,"send":never},
    "$implementation":{"property":"","default":"Homematic","format":"","requited":false,"send":onChange},
    "$nodes":{"property":"","default":[],"format":"","requited":false,"send":never},
    "uptime":{"property":"","default":0,"format":"","requited":false,"send":never},
    "interval":[
        {"property":"deviceType=HM-RCV-50","value":"","default":0,"format":"","requited":false,"send":allways},
        {"property":"deviceType=HM-CC-RT-DN","value":"","default":1200,"format":"","requited":false,"send":allways},
        {"property":"deviceType=HM-WDS30-OT2-SM","value":"","default":1800,"format":"","requited":false,"send":allways},
        {"property":"deviceType=HMIP-PSM","value":"","default":600,"format":"","requited":false,"send":allways},
        {"property":"deviceType=HMIP-PS","value":"","default":0,"format":"","requited":false,"send":allways},
        {"property":"deviceType=HM-Sec-SCo","value":"","default":0,"format":"","requited":false,"send":allways}
    ],
    "freeheap":{"property":"","default":0,"format":"","requited":false,"send":never},
    "cpuload":{"property":"","default":0,"format":"","requited":false,"send":never},
    "battery":[
        {"property":"datapoint=BATTERY_STATE","value":"value","default":0,"format":"1.5:3.1","translate":"%","requited":false,"send":allways},
        {"property":"datapoint=LOWBAT","value":"value","default":false,"format":"true:false","translate":"%","requited":false,"send":allways}
    ],
    "supply":{"property":"datapoint=BATTERY_STATE","value":"value","default":0,"format":"1.5:4.6","requited":false,"send":allways},
    "signal":{"property":"RSSI_PEER","default":0,"format":"0:100","requited":false,"send":allways},
    "name":{"property":"deviceType","default":"Homematic","format":"","requited":false,"send":onChange},
    "version":{"property":"iface","default":"0.0.1","format":"","requited":false,"send":onChange},
    "room":{"property":"room","default":"","format":"","requited":false,"send":onChange}
}

var status = {fill:"red",shape:"dot",text: "payload="+msg.payload};
var getValue = function (theMsg,property) {
    if (typeof property === "string") {
        var splitted = property.split('.');
        for (let currentObject of splitted) {
            if (theMsg.hasOwnProperty(currentObject)) {
                theMsg=theMsg[currentObject];
            } else {
                return undefined;
            }
        }
        return theMsg;
    } else node.error("[translate] property:'"+property+"' not a string");
}

var currentProperty = {};
var value;
var sendStore=context.get("sendStore") || {};
var index;

if (!msg.hasOwnProperty("state")) msg.state={};
for (let property in translator) {
    if (translator.hasOwnProperty(property)) {
        properties = (Array.isArray(translator[property])) ? translator[property] : [translator[property]]
        for (currentProperty of properties) {
            if (property==="$topic" && (Array.isArray(currentProperty.properties))) {
                msg.state[property]=""
                for (let element of currentProperty.properties) {
                    value = getValue(msg,element);
                    if (value !== undefined) msg.state[property]+=value;
                }
                index=msg.state[property];
            } else {
                if (currentProperty.hasOwnProperty("property")) {
                    if (currentProperty.property!=="") {

                        // if there is a conditional property defined
                        if (currentProperty.property.includes("=")) {
                            //node.warn([currentProperty.property,typeof currentProperty.property]);
                            let propertySplitted=currentProperty.property.split("=");
                            if (getValue(msg,propertySplitted[0])===propertySplitted[1]) {
                                if (currentProperty.translate==="%") { // if Translator for %
                                    let formatSplitted=currentProperty.format.split(":");
                                    if (formatSplitted[0]==="true" || formatSplitted[0]==="false") {
                                        let min=(formatSplitted[0]==="true") ? true : false;
                                        if (getValue(msg,currentProperty.value)===min) { // set % values for boolean values
                                            msg.state[property]=25;
                                        } else {
                                            msg.state[property]=75;
                                        }
                                    } else {
                                        // rescale percentage to given format "min:max"
                                        let min=Number(formatSplitted[0]);
                                        let max=Number(formatSplitted[1]);
                                        msg.state[property]=Math.floor(100/(max-min)*(Number(getValue(msg,currentProperty.value))-min));
                                    }
                                } else {
                                    if (currentProperty.value!=="") {
                                        msg.state[property]=getValue(msg,currentProperty.value);
                                    } else {
                                        msg.state[property]=currentProperty.default;
                                    }
                                }
                            }
                        } else {
                            if (currentProperty.property!=='') {
                                msg.state[property]=getValue(msg,currentProperty.property);
                            } else {
                                msg.state[property]=currentProperty.default;
                            }
                        }
                    } else {
                        msg.state[property]=currentProperty.default;
                    }
                    if (currentProperty.send===onChange) {
                        if (!sendStore.hasOwnProperty(index)) sendStore[index]={};
                        if (!sendStore[index].hasOwnProperty(property) || sendStore[index][property]===null) {
                            sendStore[index][property]=msg.state[property];
                            context.set("sendStore",sendStore);
                        } else {
                            if (sendStore[index][property]===msg.state[property]) {
                                // node.warn(["delete onChange",index,property,sendStore[index],sendStore[index].hasOwnProperty(property),sendStore[index][property]]);
                                delete msg.state[property]
                            } else {
                                sendStore[index][property]=msg.state[property];
                            }
                        }
                    } 
                    if (currentProperty.send===never) {
                        delete msg.state[property]
                    }
                }
                if (property==="$name") {
                    status.fill="green";
                    status.text=msg.state[property]+"="+msg.state[property];
                }
            }
            if (currentProperty.required && !msg.state.hasOwnProperty(property)) {
                status.text="required "+property+" "+msg.state[property];
            }
        }
    }
}
// check if required attributes exists
for (let property in translator) {
    if (translator[property].required) { // if any requitred property is missing dispose complete msg.state
        if (!msg.state.hasOwnProperty(property)) {
            node.warn([property+" undefined",msg.payload]);
            status.fill="red";
            status.text="required "+property+" undefined";
            delete msg.state;
            break;
        }
    }
}
msg.topic=msg.state.$topic;
node.status(status);
return msg;
